import sys,fileinputimport random as rimport math#read filef = open('data24.txt')inputVec=[line.strip().split() for line in f]f.close()#set neuron output settingsneuronW=4neuronH=4neuronN=neuronW*neuronH#init weight matrixweight=[0]*len(inputVec)for i in xrange(len(weight)):	weight[i]=[[0,0]]*neuronN#initalizing weights between -1.0 and 1.0for i in xrange(len(inputVec)):	for j in xrange(neuronN):		weight[i][j]=[r.uniform(-1.0,1.0),r.uniform(-1.0,1.0)]#competitionsmallestVal=100000000000smallestJ=0m=[]for i in xrange(len(inputVec)):	for j in xrange(neuronN):		#print (weight[i][j][0]-float(inputVec[i][0])**2)		m.append(math.sqrt(((weight[i][j][0]-float(inputVec[i][0]))**2) + (weight[i][j][1]-float(inputVec[i][1]))**2))		if(m[-1]<smallestVal):			smallestJ=j			smallestVal=m[-1]		#adjust weights