import sys,fileinputimport random as r#read filef = open('data24.txt')inputVec=[line.strip().split() for line in f]f.close()#set neuron output settingsneuronW=4neuronH=4neuronN=neuronW*neuronH#init weight matrixweight=[0]*len(inputVec)for i in xrange(len(weight)):	weight[i]=[[0,0]]*neuronN#initalizing weights between -1.0 and 1.0for i in xrange(len(inputVec)):	for j in xrange(neuronN):		weight[i][j]=[r.uniform(-1.0,1.0),r.uniform(-1.0,1.0)]		#(weight[i][j][0]-x[0])**2 + (weight[i][j][1]-x[1])**2